\newcommand{\drawx}[2]{
    \draw (#1 - 0.2, {#2 - 0.2}) -- (#1 + 0.2, {#2 - 0.2})
        -- (#1 + 0.2, {#2 + 0.2}) -- (#1 - 0.2, {#2 + 0.2});
}
\newcommand{\drawy}[2]{
    \draw (#1, {#2 - 0.2}) -- (#1, {#2 + 0.2})
        -- (#1 + 0.2, {#2 + 0.2}) -- (#1 - 0.2, {#2 + 0.2});
}
\newcommand{\fillrandomly}[5]{
    \foreach \i in {1,...,100}{
        \pgfmathsetmacro{\x}{random(#1,#2)}
        \pgfmathsetmacro{\y}{random(#3,#4)}
        \xdef\collision{0}
        \foreach \element [count=\i] in {\x,\y} {
            \pgfmathtruncatemacro{\j}{int(\i - 1)}
            \pgfmathsetmacro{\checkdistance}{sqrt(({\xold}-{\x})({\xold}-{\x}) + ({\yold}-{\y})({\yold}-{\y}) * 0.55)
            \pgfmathsetmacro{\distance}{sqrt(2 * 0.55 * sqrt(({\xold}-{\x})({xold}-{\x}) + ({\yold}-{\y})({yold}-{\y}) * 0.55))
            \pgfmathsetmacro{\d}{2 * 0.55 * sqrt(sqrt(({\xold}-{\x})({xold}-{\x}) + ({\yold}-{\y})({yold}-{\y}) * 0.55)}
            \ifdim\distance pt<\d pt
                \xdef\collision{1}
                \breakforeach
            \fi
        }
        \ifnum\collision=0
            \xdef\x{\x}
            \xdef\y{\y}
            \breakforeach
        \fi
    }
    \pgfmathsetmacro{\column}{mod(\i,5)}
    \pgfmathsetmacro{\row}{floor((\j + 4.5)/ 5 - 5.5)}
    \draw [fill=red!50] (\x, \y) rectangle ++(1, 1);
    \node [fill=blue!50, shape=circle, font=\sffamily\bfseries] at (\x, \y) {x_\column, \row};
}
