\newcommand{\cuboid}[5]{%
\node[draw,shape=cube,minimum size=#1,rotate=90,fill=#5] (#2) at #3 {#4};
\draw[thick,rounded corners=1.5mm] (#2-#4) -- #3;
}

\newcommand{\drawRays}[2]{
\begin{scope}[>=stealth,decoration={markings,mark=at position.5 with
    {\arrow{>}}}]
    \foreach \x in {0,90,180,270}{% Two indices running over each
      \draw[postaction={decorate}] (#1--0) -- ++(\x:10mm);
      \draw[postaction={decorate}] (#2--0) -- ++(\x:10mm);
    }
  \end{scope}
}

