\foreach \source in {1,...,6}
    \foreach \dest in {1,...,6,1}
        \path (I-\source) edge [bend left] (H-1-\dest);
% Connect every node in the hidden layer with the output layer
\foreach \source in {1,...,6,1}
    \foreach \dest in {1,2,3,4,5,6}
        \path (H-1-\source) edge [bend left] node [auto, swap, label neuron] {$\lambda_{\dest}$} (O-\dest);
