\node [label neuron] (O-1-label) at (H-4-3) {$\lambda_1^{(1)}$};
\node [label neuron] (O-2-label) at (H-4-4) {$\lambda_2^{(1)}$};
\node [label neuron] (O-3-label) at (H-4-5) {$\lambda_3^{(1)}$};
