\node [label neuron] (O-2-label) at (H-5-4) {$\lambda_2^{(2)}$};
\node [label neuron] (O-3-label) at (H-5-5) {$\lambda_3^{(2)}$};
% Connect the input layer with the first hidden layer
\foreach \source in {1,...,6}
    \foreach \dest in {1,...,6,1}
        \path (I-\source) edge [bend left] (H-2-\dest);
% Connect the second hidden layer with the first hidden layer
\foreach \source in {1,...,6,1}
    \foreach \dest in {1,...,6,1}
        \path (H-2-\source) edge [bend left] (H-1-\dest);
% Connect the last hidden layer with the output layer
