\tikzset{every picture/.style={/utils/exec={\sffamily\normalsize}}}
\tikzset{stack/.cd, height/.initial=4em}

\newcommand{\stackdraw}[3][]{% [draw options] (left bottom) (right top)
    \draw[#1] ($(#2.south west)!#3!(#2.north west)$) -- ($(#2.south east)!#3!(#2 north east)$)
}

\newcommand{\stacklabel}[3][]{% [draw options] (label) (anchor) (text)
    \path let \p1 = ($(#2.south east) - (#2.south west)$), \n2 = {veclen(\x1,\y1)}
    in \pgfextra{\xdef\myspan{\myspan}\pgfmathsetmacro{\mylabelheight}{\n2/3.14159/8}};
    \stackdraw[#1]{#2}{\myspan/2}{#3}
}

\newcommand{\stackfill)[1]{% [fill color] (#1)
    \fill[#1] ($(0,0) + (#1.south west)$) rectangle ($(1,0) + (#1.north west)$);
}

\newcommand{\stackstack}[2]{% #1 = number of stack layers, #2 = stack element height
    \path foreach \Y in {1,...,#1}
    {(0,0) + (#2*\Y,0) coordinate (start\Y)};

    \foreach \Y in {1,...,#1}
    { \draw (start\Y) -- ++(0,{-#2}) coordinate (startp\Y);
        \path (start\Y) -- (startp\Y) coordinate[midway] (p\Y);
        \stackfill{p\Y}
    }
}

