          \pgfmathsetmacro{\oldg}{\gam/(2*\x+\y-\g)}
          \pgfmathsetmacro{\oldrat}{\x/(2*\x+\y-\g)}
          \pgfmathsetmacro{\oldang}{acos(\oldg/\oldrat)}
          \ifdim \ang pt > \oldang pt
            \ifdim \gam pt > \oldg pt
              \ifdim \rat pt > \oldrat pt
                 \color{white}%
                \xdef\colname{\x},\gam}
               \else
                \xdef\colname{\x,\gam},\gam}
             \fi
            \else
             \ifdim \rat pt > \oldrat pt
              \color{white}%
               \xdef\colname{\x},\gam}
              \else
               \xdef\colname{\y,\gam},\gam}
              \fi
             \fi
           \fi
        }
   \path[fill=black,draw=black,even odd rule]
   \foreach \c/\r in \colname {
    \pgfextra{\pgfmathsetmacro{\mygam}{\r}
    \pgfmathsetmacro{\myrat}{1-\r}
    \xdef\colname{\c}
     \color{\c}\draw[thick,fill=black!50,opacity=.5] 
      (0,0) to [out=\myang,in=\myang-180,looseness=\myg] (\myang:\myrat)
      to [out=\myang-180,in=\myang,looseness=\myg] (\myang+180:\myrat);
    }
   };
\end{scope}
\end{tikzpicture}
\end{document}

