\begin{tikzpicture}[auto,
    dot/.style = {
        draw,
        fill = white,
        circle,
        inner sep = 2pt
    },
    ]
    \def\rcirc{3cm}
    \def\rdot{5mm}
    \def\rsmall{2cm}
    \def\rbig{2.5cm}

    % Points
    \coordinate (O) at (0,0);
    \node (P1) [label=right:$P_1$] at ($(O) + (\rcirc,0)$) {};
    \node (P2) [label=left:$P_2$] at ($(O) + (\rcirc,-\rsmall)$) {};
    \node (Pdots) [label=right:$\dots$] at ($(P1)!0.5!(P2) + (\rdot,0)$) {};
    \node (P3) [label=right:$P_3$] at ($(Pdots) + (\rdot,0)$) {};
    \node (Pdots2) [label=right:$\dots$] at ($(Pdots)!0.5!(P3) + (\rdot,0)$) {};

    % Circles
    \node (C2) [circle,draw,minimum width=\rsmall,fill=black] at ($(Pdots2) + (\rsmall,0)$) {};
    \node (C1) [circle,draw,minimum width=\rcirc,fill=black] at ($(Pdots2) + (\rsmall,-\rsmall)$) {};
    \node (C3) [circle,draw,minimum width=\rbig,fill=black] at ($(P3)+(0,\rbig)$) {};

    % Dots
    \node (D1) [dot,label=below:$\beta_1$] at ($(C2) + (-\rdot,0)$) {};
    \node (D2) [dot,label=below:$\beta_2$] at ($(C1) + (-\rdot,0)$) {};
    \node (Ddots) [dot,label=below:$\dots$] at ($(C3) + (-\rdot,0)$) {};
    \node (D3) [dot,label=below:$\beta_k$] at ($(C3) + (\rdot,0)$) {};

    % Arrows
    \draw[->,shorten >=2pt] (D1) -- (C1);
    \draw[->,shorten >=2pt] (D2) -- (C2);
    \draw[->,shorten >=2pt] (Ddots) -- (C3);
    \draw[->,shorten >=2pt] (D3) -- (C3);

    % Text
    \node (n1) at ($(C1) + (-\rdot/2,0)$) {$r_1$};
    \node (n2) at ($(C2) + (-\rdot/2,0)$) {$r_2$};
    \node (n3) at ($(C3) + (0,-\rdot/2)$) {$r_k$};

    \draw (D1) -- (P1);
    \draw (D2) -- (P2);
    \draw (Ddots) -- (Pdots);
    \draw (D3) -- (P3);

    \draw[<-] (C1) -- ++(0,-3cm) node[right] {$z_1$};
    \draw[<-] (C2) -- ++(0,-3cm) node[right] {$z_2$};
    \draw[<-] (C3) -- ++(0,-3cm) node[right] {$z_k$};
    \node (Cdots) [right=2.5cm of $(C2) + (-3cm,0)$] {\ldots};
\end{tikzpicture}
\end{document}

