% Multi-parameters macro
\newcommand{\multiparameters}[2][5]{%
    \pgfmathsetmacro{\scalingfactor}{#1} % Scale in cm
    \begin{tikzpicture}[
        scale=\scalingfactor,
        d/.style={circle, draw=black!30, minimum size=1cm,
        inner sep=0pt}] % Defining nodes style
        % Nodes list
        \def\nodeslist{#2} % Define list in cmd 
    \foreach \n in \nodeslist {%
        % From x/y we get x'(s) and y'(s)
       \pgfmathsetmacro{\xprime}{\n[x]} 
       \pgfmathsetmacro{\yprime}{\n[y]}
       % Now we draw the lines
       \draw (\xprime,\yprime) -- (\xprime,-\yprime);         
       \draw (-\xprime,\yprime) -- (-\xprime,-\yprime);
       \draw (\yprime,-\xprime) -- (-\yprime,\xprime);             
    }% 
    \end{tikzpicture}%
}%

