\newcommand{\syllable}[3][30]{
    \foreach [count=\j] \i/\l in {#3}{
        \draw (\j*\l-0.55,-0.6) node [neuron] (neuron-#3-\j) {$\spectral{#2}{\j}$};
        \ifthenelse{\j = 1 }{
            \draw [stateTransition] (neuron-#3-\j) -- (state-#3-\j)
                node [above, very near start] {\tiny \spectral{#2}{\j}};
        }{
            % uncomment this to see which ones are connected:
            %\draw [thin,gray]           
            %            (neuron-#3-\j) -- (neuron-#3-\i)
            %            (state-#3-\j) -- (state-#3-\i);
            %% evaluate start position:
            \coordinate (start-#3-\j) at (
                (-3+1.06+\j*\l/1.55,0.68+\j*0.25+\j*1.62/1.1));
        }
    }
}


\newcommand{\layer}[2][30]{
    %% start in the back and work forward:
    \foreach [count=\p] \name/\offset in {#2}{
        %% print the name and offset
        \node at start-#2-\p   {{$\name +_\offset$}};
        %% draw backwards if specified level is '30':
        \ifthenelse{\pgfmatrixcurrentband=28      
                {\name +_\offset}   < 9}
            {\draw backwards:\ layer[-30]{\name/'s }}       
            {\draw :\layer{\name/'s }}   
        %% draw forward:
        \draw ((state-\pgfmatrixcurrentband-\p|-start-#2-\p) --
            ++ (-9+1+\p*\l/1.4,0);
    }


