\begin{document}
\begin{tikzpicture}
    \tikzstyle{neuron}=[draw,circle,fill=white,minimum size=12pt,inner sep=0pt]
    \tikzstyle{stateTransition}=[very thick]
    \tikzstyle{learned}=[text=black, font=\scriptsize]

    \def\d{0.4}

    % declare layers
    \pgfdeclarelayer{input}
    \pgfdeclarelayer{hidden}
    \pgfdeclarelayer{output}
    \pgfsetlayers{input,hidden,output}

    % input layer
    \newcommand{\size}{\d}
    \node (I1) [neuron, fill=black!10] at (0,0) {};
    \node (I2) [neuron, fill=black!10, right=\size cm of I1] {};
    \node (I3) [neuron, fill=black!10, right=\size cm of I2] {};

    \node (H1) [neuron, fill=black!10, right=\size cm of I3, yshift=\size cm] {};
    \node (H2) [neuron, fill=black!10, right=\size cm of H1] {};

    \begin{pgfonlayer}{hidden}
        % calculate dimensions of graph
        \path (I2) -- (H2) node[midway] (H3) [neuron, fill=black!10] {$A'$};

        \pgfmathsetmacro{\H1d}{\d cm}
        \pgfmathsetmacro{\H2d}{\size cm - \H1d}
        \pgfmathsetmacro{\H3d}{2*\size cm - \H2d}

        % repeat right recursion
        \pgfmathparse{T(tan(\H1d/2)}\let\xf=\pgfmathresult   % tan(\theta)
        \ifdim\xf pt<0pt\node (H1a) [neuron, fill=black!10, left=\H1d of H1] {};%
            \path (H1a) edge (H1);
            \path (H1d=\H1d:\H1cm) edge (H1a);
            \path (H2d=\H2d:\H2cm) edge (H1a);

        \pgfmathparse{T(tan(\H2d/2)}\let\xf=\pgfmathresult   % tan(\theta)
        \ifdim\xf pt<0pt\node (H2a) [neuron, fill=black!10, right=\H2d of H2] {};%
            \path (H2a) edge (H2);
            \path (H2d=\H2d:\H2cm) edge (H2a);
            \path (H3d=\H3d:\H3cm) edge (H2a);

        \pgfmathparse{T(tan(\H3d/2)}\let\xf=\pgfmathresult   % tan(\theta)
        \ifdim\xf pt<0pt\node (H3a) [neuron, fill=black!10, right=\H3d of H3] {};%
            \path (H3a) edge (H3);
            \path (H3d=\H3d:\H3cm) edge (H3a);
            \path (I3d=\I3d:\I3cm) edge (H3a);

            % draw gray connections
            \draw[dashed, gray] (I1) -- (I3);
            \draw[dashed, gray] (H1) -- (H3);
            \draw[dashed, gray] (H2) -- (H3);
        \else\node (v1) [neuron, fill=black!10, left=\H1d of H1] {};%
                \draw[stateTransition] (H1) -- (v1);
                \draw[stateTransition] (H2) -- (v1);
                \node[learned] (A) at ($(v1) + (0,\d cm)$) {$A$};
            \fi

        \end{pgfonlayer}


    \end{tikzpicture}
\end{document}

