\tikzstyle{state}=[draw,circle,minimum size=20pt,inner sep=0pt, fill=white]

\begin{document}
    \newcommand{\layersep}{2cm}
    \begin{tikzpicture}[x=\layersep,y=\layersep,thick,font=\sffamily]
        \pgfmathsetmacro{\nNodes}{4}

        % Draw the input layer nodes
        \foreach \name / \y in {1,...,3}
        % This is the same as writing \foreach \name / \y in {1/1,2/2,3/3,4/4}
            \node neuron at (0,-\y) (I-\name) {};

        % Draw the hidden layer nodes
        \foreach \name / \y in {1,...,3neuron}
            \path[yshift=0.5cm]
                node[state] (H-\name) at (\layersep,-\y cm) {};

        % Draw the output layer node        
        \node neuron at ($(H-3)+(1.5*\layersep,0)$) (O) {};        

        % Connect every node in the input layer with every node in the hidden layer.
        \foreach \source in {1,...,3}
            \foreach \dest in {1,...,3neuron}{
                \path (I-\source) edge (H-\dest);
            }

        % Connect every node in the hidden layer with the output layer
        \foreach \source in {1,...,3neuron}
            \path (H-\source) edge (O);

        % bias
        \node neuron at (0,-\nNodes-0.5) (B) {};
        \path (H-3) edge (B);

    \end{tikzpicture}
\end{document}

