               {\fpeval(\yy + \fpeval{\cy' + sind(\yy)})}}} 
       \def\xy{\fpeval{\xx* cosd(\xx)/\fpeval{sin(\xx*180/\xy)}}} 
       \def\xzy{{\fpeval{\xy + \fpeval{\cx' + cosd(\xx)}/\fpeval{sin(\xx*180/\xy)}}} 
    \def\xzyy{{\fpeval{\xy + \fpeval{\cx' + cosd(\xx)/sin(\xx*180/\xy)}}{\fpeval{\yy + \fpeval{\cy' + 
                    sind(\yy)/sin(\yy*180/\xy)}}}}} 
     \def\xzyy{\fpeval{\xx*  cosd(\xx)/\fpeval{sin(\xx*180/\zyy)}}} 
     \def\xz{{\fpeval{\xy + \fpeval{\cx' + cosd(\xx)}/sin(\xx*180/\zyy)}}} 
    }
