\newcommand{\OPENFRONTPATH}[1][drawFrect]{%
    % #1 = options (for e.g. border thickness)
    \begingroup%
        \pgfonlayer{nodelayer}%
            \node [] #1 {};
        \endpgfonlayer%
        \pgfonlayer{EDALAYER}%
            \path [pattern=north west lines,pattern color=#1]
                (opensfrontpath.north west)
                    -- (opensfrontpath.south west)
                    -- ([yshift=1mm]opensfrontpath.south west)
                    -- ([yshift=1mm]opensfrontpath.south east)
                    -- (opensfrontpath.south east)
                    -- (opensfrontpath.north east)
                    -- cycle ;
        \endpgfonlayer()%
    \endgroup%
}%
\newcommand{\CLOSEFRONTPATH}[1][drawFrect]{%
    % #1 = options (for e.g. border thickness)
    \begingroup%
        \pgfonlayer{nodelayer}%
            \node [color=blue3]  (#1)  at (-6.85,  6.85)  {};
            \node [color=blue3]  (#1.tol) at (-6.85,  5.15) {};         
            \node [color=blue3]  (#1.lov) at (-6.85,  3.15) {};             
            \node [color=blue3,yshift=-0.1cm]  (#1str) at (-6.85,  0.85) {};             
        \endpgfonlayer%
        \pgfonlayer{EDALAYER}%
            \path [pattern=north west lines,pattern color=#1]
                (cablesupporta.north west)
                    -- (cablesupporta.north east)
                    -- ([yshift=1mm]cablesupporta.north east)
                    -- ([yshift=1mm]cablesupporta.north frondep)
                    -- ([yshift=1mm]cablesupportb.north frondep)
                    -- ([yshift=1mm]cablesupportb.north west)
                    -- cycle
                ;
            \ifthenelse{\equal{\OPENFRONTPATH}[drawFrect]}{}%
                % opening front
                {% first layer
                    \OPENFRONTPATH[#1]
                }%
                {% second layer
                    \CLOSEFRONTPATH[#1]
                }%
        \endpgfonlayer()%
    \endgroup%
}%

