\def\initialsegment{\path (0.0,0.0) arc (-90: 270 : \radius);}
\def\nbsegments{30} % specify the number of segments in the cycle
\def\initialangle{180} % specify the angle of the first point
\def\solutelength{0.3cm} % specify the length of each segment in pt (cannot be greater than 1cm)
\def\finalsegment{\path (0.0,0.0) arc (-90: 90 : \radius)};

