       arc (\initialangle:{360-(\initialangle+\nbsegments/2)}:\radius)
       node[label= { [inner sep=0.5pt]center:{}}]{};
    %% switch to low opacity to make it easier to see the endpoints
    \path[transparency group,opacity=0.2]\finalsegment;
