\newcommand\DrawFracPlane[5]{ % H, W, D, L, P
  \pgfmathsetmacro\H{#1/2}                          % scale
  \pgfmathsetmacro\W{#2/2}                          % scale
  \pgfmathsetmacro\D{#3/2}                          % scale
  \pgfmathsetmacro\L{#4/2}                          % scale
  \pgfmathsetmacro\P{#5/2}                          % scale
  \filldraw [fill=cyan!35!blue, draw=black] (0,0,\P) -- (0,0,-\P) -- (\W,0,-\P) -- (\W,0,\P) -- cycle;
  \filldraw [fill=orange!10!red, draw=black] (0,0,\P) -- (0,0,-\P) -- (0,-\H,-\P) -- (0,-\H,\P) -- cycle;
  \draw[thick,->] (0,0,0) -- (\W,0,0) node[anchor=west] {\textbf{n}};
  \draw[thick,->] (0,0,0) -- (0,-\H,0) node[anchor=east] {\textbf{r}};
  \draw[thick,->] (0,0,0) -- (0,0,\D) node[anchor=south] {\textbf{theta}};
  \draw [dashed] (0,0,\D) -- (\W,0,\D);
  \draw[thick,<->] (\W*1.1,0,-\P) arc (-90:0:\W*1.1 and \H*1.1);
  \pgfmathsetmacro\temp{\D-#3*\W*1.1}%
  \draw[thick,<->] (\W*1.1,\temp,-\P) arc (0:90:\W*1.1 and \H*1.1);
  \draw [dashed] (\W*1.1,\D-#3*\H*1.1) -- (\W*1.1,0,-\P);
  \node at (\W*1.2,-\H-0.4,0) {\small #1};
  \node at (\W*1.2,-\H-0.4,\D) {\small #2};
  \node at (\W*1.2,-\H,-\P) {\small #3};
  \node at (\W*1.2,-\H,\P) {\small #4};
  \node at (-0.3,-\H,-\P) {\small #5};
}

\newcommand\DrawFracPlaneCylindrical[5]{ % H, W, D, L, P
  \pgfmathsetmacro\H{#1/2}                          % scale
  \pgfmathsetmacro\W{#2/2}                          % scale
  \pgfmathsetmacro\D{#3/2}                          % scale
  \pgfmathsetmacro\L{#4/2}                          % scale
  \filldraw [fill=cyan!35!blue, draw=black] (0,0,\P) -- (0,0,-\P) -- (\W,0,-\P) -- (\W,0,\P) -- cycle;
  \filldraw [fill=orange!10!red, draw=black] (0,0,\P) -- (0,0,-\P) -- (0,-\H,-\P) -- (0,-\H,\P) -- cycle;
  \draw[thick,->] (0,0,0) -- (\W,0,0) node[anchor=west] {\textbf{n}};
  \draw[thick,->] (0,0,0) -- (0,-\H,0) node[anchor=east] {\textbf{r}};
  \draw[thick,->] (0,0,0) -- (0,0,\D) node[anchor=south] {\textbf{theta}};
  \draw [dashed] (0,0,\D) -- (\W,0,\D);
  \draw[thick,<->] (\W*1.1,0,-\P) arc (-90:0:\W*1.1 and \H*1.1);
  \pgfmathsetmacro\temp{\D-#3*\W*1.1}%
  \draw[thick,<->] (\W*1.1,\temp,-\P) arc (0:90:\W*1.1 and \H*1.1);
  \draw [dashed] (\W*1.1,\D-#3*\H*1.1) -- (\W*1.1,0,-\P);
  \draw[thick,<->] (\W*1.1,0,\P) -- (\W*1.1,0,-\P) node[anchor=west] {\small $\theta_{\mathsf{atm}}$};
  \draw[thick,<->] (\W*1.1,\D-#3*\W*1.1,0) -- (\W*1.1,0,0) node[anchor=west] {\small $\tau_{\mathsf{atm}}$};
  \draw[thick,<->] (\W*1.1,\D-#3*\W*1.1,\P) -- (\W*1.1,0,\P) node[anchor=west] {\small $\phi_{\mathsf{atm}}$};
}

\newcommand\FracCylindrical[1]{\DrawFracPlane{#1}{1.5}{1}{1}{#1}}
\newcommand\FracCylindricalBissection[2]{\DrawFracPlane{#1}{1.5}{#2}{#2+1}{#1}}

