\newcommand{\DrawNodes}[2][]{%
\foreach \x [evaluate=\x as \y using {-0.1*\x}] in {1,2,3,4}
  {
  \node[#1] (z\x) at (\y,0) {$x_\x$};
  \node[#1] (p\x) at (\y,1) {$\partial x_\x$};
  \node[#1] (q\x) at (\y,2) {$q_\x$};
  \node[#1] (v\x) at (\y,3) {$v_\x$};
  \node[#1] (r\x) at (\y,4) {$r_\x$};
  \node[#1] (u\x) at (\y,5) {$u_\x$};
  \node[#1] (p\x i) at (\y,6) {$\partial u_\x$};
  \node[#1] (u\x j) at (\y,7) {$\partial v_\x$};
  \node[#1] (z0) at (0,\y) {$z_0$};
  \node[#1] (v\x j) at (0,\y -1) {$v_\x$};
  \node[#1] (r\x j) at (0,\y -2) {$r_\x$};
  \node[#1] (q\x j) at (0,\y -3) {$q_\x$};
  \node[#1] (p\x k) at (0,\y -4) {$\partial q_\x$};
  \node[#1] (p\x e) at (0,\y -5) {$\partial u_\x$};
}

\newcommand{\DrawEdgesAndNodes}[3][]{%
\foreach \t [evaluate={%
  \c=(#2 - #1);
  \x=0.5 * (#2 - #1); \y=0.5 * (#3 - #1);}] in {#1,...,#3}
  {
  \draw[#3] (#1,\c) to[out=0,in=0] (\x,\y) to[out=180,in=180] (\x+1,\c + 1) to[out=0,in=0] (\x + 1.5,\y) to[out=180,in=0] (\x + 2,\c + 1) to[out=180,in=0] (\x + 2,\c);
  \draw[-{Implies[]} #3] (\x,\c) to[out=0,in=0] (\x + 1,\c + 1);
  \node[#1] (a) at ($(\x,\c)+(0.25,0.5)$) {};
  \node[#2] (b) at ($(\x + 1,\c +(1.25,0.5)$) {};
  \node[#3] (c) at ($(\x + 1.75,\c +(1.25,0.5)$) {};
  \node[#3] (d) at ($(\x + 2,\c +(1.75,0.5)$) {};
  \draw[-{Implies[]} #3] (a) -- (\x,\c);
  \draw[-{Implies[]} #3] (b) -- (\x + 1,\c + 1);
  \draw[-{Implies[]} #3] (c) -- (\x + 1.75,\c + 1);
  \draw[-{Implies[]} #3] (d) -- (\x + 2,\c + 1);
  }
}

\newcommand{\DrawNodesAndEdges}[2][]{%
\foreach \x [evaluate=\x as \c using {int(\x - 1)}] in {1,...,#1}
  {
  \draw[thick,#2] (\c,\c) circle [radius=1];
  \DrawEdgesAndNodes{\c}{\x}
  }
}

