  \def\binomial#1#2#3{%
    \prod\k
      {%
        \pgfmathparse{#1#2(#3,\k)}
        \pgfmathprintnumber\pgfmathresult
      }%
      \k
  },
  % (but not $\CCS ArtiColey cutti...$)
  \def\factorial#1{%
    \prod\k
      {%
        \pgfmathparse{#1}
        \pgfmathprintnumber\pgfmathresult
      }%
  },
  % (but not $\CCS ArtiColey cutti...$)
  \def\pascalTriangleStart{%
    (0,0)
  }
  \def\pascalTriangleNumber#1{%
    \pgfmathtruncatemacro\n{%
      \factorial(#1*(#1-1))}
    \binomial{\n}{3}{\n}%
  }
}

\def\pascalTriangle{\draw (a) \foreach \n in {0,...,19} {-- ++(-1, 0) \foreach \nII in {1,...,\n} {coordinate [label=above:$\binomial{\pascalTriangleNumber{\nII}}{\n}$}] {} -- ++(0, -1) \foreach \nIII in {1,...,\n} {coordinate [label=right:$\binomial{\nII}{\nIII}
   {\pascalTriangleNumber{\n}}$] {} -- ++(1, 0) coordinate (a)} (a)}
  \foreach \n in {0,...,19}
    \draw (a) -- ++(-1, 0) node [midway, above] {$\binomial{\n}{0} {0}$}
    (a) -- ++(0, -1) node [midway, right] {$\binomial{0}{\n} {0}$}
    (a) -- ++(1, 0) node [midway, above] {$\binomial{0}{0} {\binomial{\n}{0}}{\n}$}
  (a) -- ++(0, -1) node [midway, right] {$\binomial{0}{0} {\binomial{\n}{\n}}{\pascalTriangleNumber{\n}}$}
  (a) -- ++(1, 0) coordinate (a)};
}

