]

   % Draw the input layer nodes
   \foreach \name / \y in {1,...,3}
   \node[input neuron, pin=left:Input \#\y] (I-\name) at (0,-\y-2.5) {};  
   % set number of hidden layers to 8
   \newcommand\Nhidden{8}

   % Draw the hidden layer nodes
   \foreach \N in {1,...,\Nhidden} {
   \foreach \y in {0,...,5} { %2 + \y
     \ifnum \y=4
       \ifnum \N>3
         \node at (\N*\layersep,-\y*\nodeinlayersep) {$\vdots$};
       \else
         \node at (-2*\layersep,-\y*\nodeinlayersep) {$\vdots$};
       \fi
     \else
         \ifnum \y=0 % special case
           \node[neuron] (H\N-\y) at (\N*\layersep,-\y*\nodeinlayersep ) {$\frac{1}{1+e^{-x}}$};
         \else
           \node[hidden neuron] (H\N-\y) at (\N*\layersep,-\y*\nodeinlayersep ) {$\frac{1}{1+e^{-x}}$};
         \fi
     \fi
     \ifnum \y=3
       \ifnum \N>3
         \node at (\N*\layersep,-\y*\nodeinlayersep ) {$\vdots$};
       \else
         \node at (-2*\layersep,-\y*\nodeinlayersep ) {$\vdots$};
       \fi
     \else
       \node[neuron] (H\N-\y) at (\N*\layersep,-\y*\nodeinlayersep ) {$\frac{1}{1+e^{-x}}$};
     \fi
     }
   }
   % Draw the output layer node
   \node[output neuron,pin={[pin edge={->}]right:Output}, right of=H\Nhidden-3] (O) {};  
   % set number of hidden layers to 8
   \newcommand\Nhidden{8}

   % Connect every node in the input layer with every node in the hidden layer.
   \foreach \source in {1,...,3}
   \foreach \dest in {1,...,3,5} {
     % \path[yellow] (H-0.south) edge (H1-\dest);
     \path[gray] (I-\source) edge (H1-\dest);
     \path[dashed,gray] (H1-\source) edge (H2-\dest);
   }

   % connect all hidden stuff
   \foreach [remember=\N as \lastN (initially 1)] \N in {2,...,\Nhidden}
   \foreach \source in {0,...,3,5} 
   \foreach \dest in {1,...,3,5}{

     \ifnum \source=0
       \ifnum \dest=0
         \path (H\lastN-\source) edge (H\N-\dest);
               \else
         \path (H\lastN-\source) edge (H\N-\dest);
               \fi
     \else
       \ifnum \dest=0
         \path (H\lastN-\source) edge (H\N-\dest);
       \else
         \path (H\lastN-\source) edge (H\N-\dest);
        \fi 
     \else
       \ifnum \source=5 \ifnum \dest=5
         \path (H\lastN-\source) edge (H\N-\dest);
       \else
         \path (H\lastN-\source) edge (H\N-\dest);
        \fi 
       \else
         \path (H\lastN-\source) edge (H\N-\dest);
       \fi
     \fi 

   }
   % Connect every node in the hidden layer with the output layer
   \foreach \source in {1,...,3,5}
   \path (H\Nhidden-\source) edge (O);

