   \node[annot] at ($(h4.south)+(0,-1)$) {Symbols};

   \node[annot,below of=H5] (h5) {Hidden layer};
   \node[annot,left of=h5] {Input layer};
   \node[annot] at ($(h5.south)+(0,-1)$) {Symbols};

   \node[annot,below of=O] (out) {Output layer};
   \node[annot,left of=out] {Symbols};

