\newcounter{colcount}
\newcommand\totalwidth[1]{\pgfmathtruncatemacro\totalwidth{#1}\totalwidth}
\newcommand\totalheight[1]{\pgfmathtruncatemacro\totalheight{#1}\totalheight}

\newcommand\colorrulemix[1]{%
  \def\crl{\ifnum#1=0\else\relax\fi}%
  \colorlet{mix}{\crl}%
  \begin{tikzpicture}[shift={(-10pt,-15pt)}]
    \path[fill=black!\crl] (0,0) rectangle (10,2);
    \path[draw=white,line width=4pt] (0,2) -- ++(0,1);
    \path[fill=mix] (0,1) rectangle (5,2);
    \path[draw=white,line width=4pt] (5,1) -- ++(0,1);
    \path[fill=white] (10,1) rectangle ++(\totalwidth+10pt,\totalheight+1pt);
  \end{tikzpicture}%
}

\newcommand\colorrule[1]{%
  \def\crl{\ifnum#1=0\else\relax\fi}%
  \begin{tikzpicture}[shift={(-10pt,-15pt)}]
    \path[fill=black!\crl] (0,0) rectangle (10,2);
    \path[draw=white,line width=4pt] (0,2) -- ++(0,1);
    \path[fill=mix] (0,1) rectangle (5,2);
    \path[draw=white,line width=4pt] (5,1) -- ++(0,1);
    \path[fill=white] (10,1) rectangle ++(\totalwidth+10pt,\totalheight+1pt);
    \pgfmathsetmacro\leftpos{(\totalwidth>1)*(\totalwidth+1)/2}
    \pgfmathsetmacro\rightpos{\totalwidth > 1?(\totalwidth+1)/2:\leftpos}
    \setcounter{colcount}{0}
    \foreach \val in {#1}{\stepcounter{colcount}}
    \coordinate (c\the\value{colcount}) at (\leftpos,-\thecolcount*2);
    \coordinate (r\the\value{colcount}) at (\rightpos,-\thecolcount*2);
    \ifcase\value{colcount}
    \or
      \colorrulemix{0}
    \or
      \colorrule{\val}
    \or
      \colorrulemix{0}\colorrule{\val}
    \or
      \colorrule{\val}
    \or
      \colorrulemix{0}\colorrule{\val}
    \fi
  \end{tikzpicture}%
}

