\begin{document}
    \begin{tikzpicture}[minimum width=4em] 
        \tikzstyle{inputNode} = [circle, draw=black!60, fill=black!20, inner sep=0pt, minimum size=4.5em]
        \tikzstyle{stateTransition}=[->]
        \tikzstyle{hiddenNode} = [rectangle, draw=black!60, fill=black!20, inner sep=0pt, minimum size=4em]
        \tikzstyle{outputNode} = [circle, draw=black!60, fill=black!20, inner sep=0pt, minimum size=4.5em]
        \tikzstyle{bias}=[circle, minimum size=5pt, fill=black!50]
        \tikzstyle{layerLabel} = [above, font=\large]
        \tikzstyle{biasLabel} = [above, font=\large]
        \tikzstyle{inputLabel} = [below, font=\large]
        \tikzstyle{outputLabel} = [above, font=\large]

        % Draw input layer nodes
        \foreach \name / \y in {1,...,8}
        % This is the same as writing \foreach \name / \y in {1/1,2/2,3/3,4/4}
        \node [inputNode] at (0, -\y) (i-\name) {$q_\y$};

        % Draw hidden layer nodes
        \foreach \name / \y in {1,...,11}
        \path[yshift=1cm]
            node [hiddenNode] at (3, -\y cm) (h-\name) {$x_\y$}
            edge [stateTransition] node[left, very near start] {first hidden layer} (i-\name);
        % Draw output layer nodes
        \foreach \name / \y in {1,...,5}
            node [outputNode] (o-\name) at (9, -\y cm) {$q_\y$};

        % Connect every node in the input layer with every node in the
        % hidden layer.
        \foreach \source in {1,...,8}
            \foreach \dest in {1,...,11}
                \path[stateTransition] (i-\source) edge (h-\dest);

        % Connect every node in the hidden layer with the output layer
        \foreach \source in {1,...,11}
            \path[stateTransition] (h-\source) edge (o-\source);

        % Reversed the input layer nodes with the hidden layer nodes.
        \foreach \source in {1,...,8}
            \foreach \dest in {1,...,11}
                \path[stateTransition] (h-\source) edge (i-\dest);

        % Connect every node in the hidden layer with the output layer
        \foreach \source in {1,...,11}
            \path[stateTransition] (h-\source) edge (o-\source);

        % Connect every node in the output layer with the output label
        \foreach \source in {1,...,5}
            \path[stateTransition] (o-\source) edge node[above, very near start] {output \source} (outputLabel);

        % Connect every node in the hidden layer with a bias
        \foreach \source in {1,...,11}
            \path[stateTransition] (h-\source) edge [stateTransition] node[left, very near start] {first bias} (bias);

        % Connect every node in the output layer with a bias
        \foreach \source in {1,...,5}
            \path[stateTransition] (o-\source) edge [stateTransition] node[left, very near start] {second bias} (bias);

        % Connect every node in the input layer with the bias
        \foreach \source in {1,...,8}
            \path[stateTransition] (i-\source) edge [stateTransition] node[left, very near start] {third bias} (bias);

        % Connect every node in the hidden layer with the bias
        \foreach \source in {1,...,11}
            \path[stateTransition] (h-\source) edge [stateTransition] node[left, very near start] {fourth bias} (bias);

        % Connect every node in the output layer with the bias
        \foreach \source in {1,...,5}
            \path[stateTransition] (o-\source) edge [stateTransition] node[left, very near start] { fifth bias} (bias);

        % Connect every node in the bias with the output label
        \foreach \source in {1,...,5}
            \path[stateTransition] (bias) edge (outputLabel);
    \end{tikzpicture}
\end{document}

