\newcommand\reuleauxpolypathred[5]{
  % initial direction
  \coordinate (0) at #2;
  \def\totalangle{0}
  \def\startangle{0}
  \foreach \angle in {#5}{
    \pgfmathparse{\totalangle+\angle}
    \global\let\totalangle\pgfmathresult
    \pgfmathparse{\startangle+\angle}
    \global\let\startangle\pgfmathresult
    \path[draw] (#2) -- ($(#2)+(\totalangle:#3)$) coordinate (#4-\totalangle);
  }
}
\newcommand\reuleauxpolypath[2]{
  % initial direction
  \coordinate (0) at #2;
  \def\totalangle{0}
  \def\startangle{0}
  \foreach \i in {1,2,3,4,5,6}{
    \pgfmathparse{\totalangle+\i*360/7}
    \global\let\totalangle\pgfmathresult
    \pgfmathparse{\startangle+\i*360/7}
    \global\let\startangle\pgfmathresult
    \path[draw] (#2) -- ($(#2)+(\totalangle:#1)$) coordinate (#2-\i);
  }
}
