% macro for the axis environment:
\newenvironment{myaxis}[1]
{
 \begin{scope}[shift={(0,0)}]
  \ifx#1x
   \else
    \ifx#1xmin#1xmax
     \coordinate (x) at (#1,xmax);
    \else
     \coordinate (x) at (#1,x);
    \fi
   \fi
  \axiomspt{#1}
  \fi
 }
{
 \end{scope}
}

% macro for the arrow:
\newcommand{\arrowa}[1]{\draw[->,very thick](x) -- +(45:#1);}

% define the action of the point on the axis:
\def\axiomspt#1{%
  \raisebox{0pt}[\height][0pt]{%
   \parbox{0pt}{\hss%
     $\plotpoints{#1}{#1}$\hss}%
   }%
}

