\begin{tikzpicture}[
    tdplot_main_coords,
    scale=3,
    >=stealth,
    axis/.style={->},
    vector/.style={-stealth,very thick},
  ]
  %draw the main coordinate frame axes
  \draw[axis] (0,0,0) -- (3.5,0,0) node [right] {$\mathrm{Phi}$};
  \draw[axis] (0,0,0) -- (0,2.5,0) node [above] {$\mathrm{Theta}$};
  \draw[axis] (0,0,0) -- (0,0,2.5) node [above] {$\mathrm{Psi}$};

  % draw black arc
  \begin{scope}[canvas is xy plane at z=0]
    \draw [very thick,fill=yellow!20] (0,0) circle (1);
  \end{scope}

  % draw the bending momentum vector
  \tdplotsetcoord{P}{1.6}{55}{55}
  \draw[vector,orange] (0,0,0) -- (P) node [midway,below,yshift=-0.5em] {$\mathrm{F_{Bz}}$};
  \tdplotsetthetaplanecoords{\angPhi}
  \tdplotdrawarc[tdplot_rotated_coords,color=blue,fill=blue!20,opacity=0.5]{(0,0,0)}{\angPhi}{0}{360}{}{}
  \draw[thick,loosely dashed, color=green!70!black, opacity=0.7] (0,0,0) -- (P);

  \foreach \i in {10,30,...,360}
    \draw[thick,dashed,->] (0,0,0) -- (\i:1);

  % draw the total field strength
  \pgfmathparse{sqrt((2*cos(50))*cos(2*cos(50))-2*cos(50)*sin(2*sin(50))+2*sin(50))}/2
  \let\RY=\pgfmathresult
  \draw[thick] (0,0,0) -- (2*cos(50)*sin(2*sin(50))+1,\RY,0) node[right] {$\mathrm{E}$};
  \draw[thick] (0,0,0) -- (0,2*cos(50)*sin(2*sin(50))+1,\RY) node[above] {$\mathrm{D}$};
  \draw[thick] (0,0,0) -- (0,0,2*cos(50)+1) node[above] {$\mathrm{H}$};
\end{tikzpicture}

