%\setbool{DEBUG}{false}

\newcommand{\drawLine}[2]{%
    \ifbool{DEBUG}{%If True
        \node [above] at (#1,#2) {Line #2 Starting};
    }{%If False
        \draw (#1,#2) -- (#1,#2);
    }%
}

\newcommand{\drawHorizontalStripes}[2]{%
    \ifbool{DEBUG}{%If True
        \node [fill=red,anchor=south west] at (#1,#2) {};
        \node [fill=blue,anchor=north west] at (#1,#2) {};
    }{%If False
        \draw (#1,#2) rectangle (#1 + 1,#2 + 1);
    }%
}

\newcommand{\drawVerticalStripes}[2]{%
    \ifbool{DEBUG}{%If True
        \node [fill=blue,anchor=north west] at (0.5,#1) {};
        \node [fill=red,anchor=south west] at (0.5,#1) {};
    }{%If False
        \draw (0.5,#1) -- (#1,#1);
    }%
}

\newcommand{\stripesAlongVerticalLine}[4]{%
    \ifbool{DEBUG}{%If True
        \node [above] at ({#1+#3},#2) {Stripes #4};
    }{%If False
        \draw (#1,#2) -- (#1+#3,#2);
    }%
}

\newcommand{\stripesAlongHorizontalLine}[4]{%
    \ifbool{DEBUG}{%If True
        \node [fill=red,anchor=north west] at ({#1+#1},{#2+0.5}) {Stripes #4};
    }{%If False
        \draw ({#1+#1},#2) -- ({#1+#1},{#2+0.5});
    }%
}

