\begin{tikzpicture}[scale=2]
\def\total{4}
\pgfmathsetmacro\divTotal{10}
\foreach \x in {1,..., \total}{
  \pgfmathsetmacro\qdiv{2}
  \pgfmathsetmacro\itmp{1}
  \pgfmathsetmacro\imtmp{\itmp + 1}
  \pgfmathsetmacro\ihigh{floor(\x/\divTotal) - 1}
  \pgfmathsetmacro\ihighColor{ifthenelse(\ihigh < 0, -10, 0)}
  \pgfmathsetmacro\ihighText{ifthenelse(\ihigh >= 0, -10, 0)}

    \node[left] at (0,\x) {\scriptsize $t=\x$};
    \draw[blue] (0,\x) -- (1,\x);

    \foreach \y in {1,..., \divTotal}{
       \pgfmathtruncatemacro\rest{\x/\divTotal}
       \pgfmathsetmacro\mystep{1/\divTotal}
       \pgfmathsetmacro\mystepColor{ifthenelse(\mystep < 0, -10, 0)}
       \pgfmathsetmacro\mystepText{ifthenelse(\mystep >= 0, -10, 0)}
       \pgfmathsetmacro\start{\imtmp*\mystep}
       \pgfmathsetmacro\finish{\imtmp*\ihighColor + (\ihighText == -1? 1 : 0)}
       \pgfmathsetmacro\step{\mystep * (\finish - \start)}
       \pgfmathsetmacro\finhigh{floor(\y/\divTotal) - 1}
       \pgfmathsetmacro\finlow{-floor(\y/\divTotal) + 1}
       \pgfmathsetmacro\finmid{floor(\start+\step)}
       \pgfmathsetmacro\fintext{ifthenelse(\finmid < 0, -10, 0)}

       \node[below] at (\start,\y) {\scriptsize $t=\y$};
       \draw[red] (\start,\y) -- (\finish,\y);
       \tikzset{xy/.style={xshift=1.5pt, yshift=-1.5pt}}
       \ifcase\ihigh
         \node[xy] at (\start,\y) {\textcolor{\ihighColor}{\finhigh}};
         \node[xy] at (\start,\y-\finlow) {\textcolor{\ihighColor}{\finmid}};
       \else
         \node[xy] at (\start,\y)       {\textcolor{\ihighColor}{\finhigh}};
         \node[xy] at (\start,\y-\finlow) {\textcolor{\ihighColor}{\finmid}};
       \fi
       \ifcase\ihigh
         \node[left] at (\start,\y) {\scriptsize $t=\itmp$};
       \else
         \node[left] at (\start,\y) {\scriptsize $t=\imtext$};
       \fi
    };
}
\end{tikzpicture}
\end{document}

