\tikzset{
  % define a family
  my grid parameters/.is family,
  % use it to define the other keys,
  my grid parameters,
  % set the default values (if a key is not defined)
  rows/.initial=1,
  columns/.initial=1,
  % set the default values if a key is defined
  rows/.append style={row #1/.initial=1},
  columns/.append style={column #1/.initial=1},
}

\tikzset{
  % the tikz picture
  pics/grid/.style={% a style (or a key) can use one parameter by default
    code={
      \tikzset{my grid parameters,#1}
      % to get the value we use \tikzset (!) with the.get syntax to put it in a macro
      \tikzset{
        rows/.get=\rows,
        columns/.get=\columns,
      } 
      % then we can use the \rows and \columns macros to draw the picture
      \foreach \i in {1,...,\columns} {
        \foreach \j in {1,...,\rows} {
          \draw (\i,\j) circle[radius=3mm];
        }
      }
    }
  }
}

