\begin{tikzpicture}[line cap=round,line join=round,% these settings make the corners of the triangles look nicer
  scale=1.5]
% draw the coordinates of the vertices
\foreach\i in {1,...,\n}
{%
  \coordinate (A\i) at (\i*\d,0); % A(i)
  \coordinate (B\i) at (\i*\d,\d);  % B(i)
  \coordinate (C\i) at (\i*\d,2*\d); % C(i)
  \coordinate (D\i) at ($ (\i*\d,0) + (30:2*\d) $); % D(i)
  \coordinate (E\i) at ($ (\i*\d,0) + (-30:2*\d) $); % E(i)
  \coordinate (F\i) at ($ (\i*\d,2*\d) + (-90:2*\d) $); % F(i)
  \coordinate (G\i) at ($ (\i*\d,2*\d) + (90:2*\d) $); % G(i)
}
% draw the lines
\foreach\i in {1,...,\n}
{%
  \draw[blue,-latex] (A\i) -- (D\i);
  \draw[blue,-latex] (B\i) -- (C\i);
  \draw[blue,-latex] (C\i) -- (F\i);
  \draw[blue,-latex] (D\i) -- (E\i);
  \draw[red,latex-] (E\i) -- (G\i);
  \draw[red,latex-] (F\i) -- (B\i);
}
% draw the nodes in the center triangle
\foreach\i in {1,...,3}
{%
  \node at (C\i)  {$\iff$};
  \node at (E\i)  {$\iff$};
  \node at (G\i)  {$\iff$};
  \node at (D\i)  {$\iff$};
}
% draw the nodes in the vertices triangles
\foreach\i in {1,...,4}
{%
  \node[draw,circle,black,fill=white] at (A\i) {\i};
  \node[draw,circle,black,fill=white] at (B\i) {\i};
  \node[draw,circle,black,fill=white] at (C\i) {\i};
  \node[draw,circle,black,fill=white] at (D\i) {\i};
  \node[draw,circle,black,fill=white] at (E\i) {\i};
  \node[draw,circle,black,fill=white] at (F\i) {\i};
}
% draw the edges
\foreach\i in {1,...,3}
{%
  \foreach\j in {1,...,3}
  {%
    \pgfmathtruncatemacro\l{\j*\n-\i-1};
    \pgfmathtruncatemacro\m{\j*\n-\i};
    \ifnum\i<3
      \draw[green!50!black] (B\i) -- (E\i);
    \fi
    \ifnum\i>1
      \draw[green!50!black] (D\i) -- (F\i);
    \fi
    \ifnum\i>2
      \draw[green!50!black] (F\i) -- (C\i);
    \fi
    \ifnum\i>3
      \draw[green!50!black] (C\i) -- (B\i);
    \fi
  }
}
% draw the numbers
\foreach\i in {1,...,\n}
{%
  \pgfmathtruncatemacro\b{\i*\n-\n/2-1};
  \pgfmathtruncatemacro\a{\i*\n-\n/2};
  \draw[red,thick] ($ (\a*\d,0) + (0,2*\d) $) node[above,black] {$\b$} -- ($ (\b*\d,0)  + (0,2*\d) $);
  \draw[red,thick] ($ (\a*\d,0) + (0,-2*\d) $) node[below,black] {$\b$}  -- ($ (\b*\d,0)  + (0,-2*\d) $);
  \draw[red,thick] ($ (\a*\d,0) + (0,0)     $) node [circle,draw,red,thick,fill=red] {$\a$} -- ($ (\a*\d,0) + (0,-2*\d) $);
  \draw[red,thick] ($ (\b*\d,0) node[above,black] {$\a$} $) node [circle,draw,red,thick,fill=red] {$\b$}  -- ($ (\b*\d,0) + (0,-2*\d) $);
  \draw[red,thick] ($ (\b*\d,0) node[below,black] {$\b$} $) node [circle,draw,red,thick,fill=red] {$\a$} -- ($ (\b*\d,0) + (0,2*\d) $);
}
% draw the ticks
\foreach\i in {1,...,4}
{%
  \pgfmathtruncatemacro\b{\i*\n-\n/2-1};
  \draw[thick,green] ($ (\i*\d,0) node[above=2pt,black] {$\i$} $) -- ($ (\i*\d,0) + (0,2*\d) $);
  \draw[thick,green] ($ (\i*\d,0) node[below=2pt,black] {$\i$} $) -- ($ (\i*\d,0) + (0,-2*\d) $);
}
\end{tikzpicture}
