\newcommand\GroupNodes[2]%
% #1 is the node name
% #2 the number of nodes
{   \ifnum#2=1
    \node[#1] (A) at (2,0) {};
    \node[#1] (B) at (2,-2) {};
    \node[#1] (C) at (2,-4) {};
    \node[#1] (D) at (2,-6) {};
    \node[#1] (E) at (2,-8) {};
    \node[#1] (F) at (2,-10) {};
    \node[#1] (G) at (2,-12) {};
    \node[#1] (H) at (2,-14) {};
    \else
    \pgfmathsetmacro{\DeltaX}{#2-1}
    \foreach \x in {1,...,\DeltaX}
    {   \pgfmathsetmacro{\xpos}{2*\x}
        \pgfmathsetmacro{\xneg}{\xpos-1}
        \node[#1] (\xpos) at (\xneg,-2*\x) {};
        \node[#1] (\xpos) at (\xpos,-4*\x) {};
        \node[#1] (\xpos) at (\xpos,-6*\x) {};
        \node[#1] (\xpos) at (\xpos,-8*\x) {};
        \node[#1] (\xpos) at (\xpos,-10*\x) {};
        \node[#1] (\xpos) at (\xpos,-12*\x) {};
        \node[#1] (\xpos) at (\xpos,-14*\x) {};
    }
    \fi
}   

