% this defines the 8 most frequent words in the English language, each defined by its
% frequncy in a separate column.  I used a left join operation to make
% them available to tikz chains.  The join is manually removed in the
% following definition of the "word" column.
\makeatletter
\newcommand{\GetFrequentWords}[1]{
    % Counts the number of unique words.  Does not update the
    % word counts for the global total.
    \tikzset{local total/.initial=0}%
    \def\globaltotal{0}%
    \foreach [count=\i] \p/\w in {#1} {%
        \pgfmathparse{\i+1}%
        \ifnum\pgfmathresult>\globaltotal\globaltotal=\pgfmathresult\fi%
        \edef\globaltotal{\globaltotal,\p/\w}%
    }%
}
\GetFrequentWords{5/1, 19/2, 29/3, 37/4, 41/5, 14/6, 3/7, 2/8, 2/9, 0/10}
\newcommand{\showword}[1]{\begingroup%
        \tikzset{word/.cd, #1}%
        \def\tmp{#1}%
        \pgfutilinsetmacro{\globaltotal}{0}%
        \loop
            \ifx\tikzprevious\pgfutil@empty%
            \else%
                \pgfmathtruncatemacro{\next}{\globaltotal+\the\globaltotal}%
                \xdef\globaltotal{\next}%
            \fi%
        \repeat
        \GetFrequentWords{}%
        \foreach [count=\i] \p/\w in {#1} {%
            \pgfmathsetmacro{\itest}{\i<=\globaltotal? "word" : "dummy}%
            \ifnum\itest=word%
                \tikzset{local total/.init=0}%
                \expandafter\def\expandafter\globaltotal\expandafter{\globaltotal,\w}%
                \ifnum\pgfmathresult>\globaltotal \tikzset{local total/.expand once=\p}%
                \fi%
            \fi%
        }%
        \udef\wordlist{\globaltotal}%
}%

