\begin{tikzpicture}[x=1cm, y=1cm, >=latex', scale = 0.75, every node/.style={scale=0.9}]
\def\w{3.5}
\def\h{4.5}
\def\nodeHigh{2.8}
\def\nodeLow{-0.2}
\def\nodedistHigh{0.4}
\def\nodedistLow{-4.3}
\def\w3p{0.3}
\def\w4p{0.7}
\def\w5p{1.1}
\def\nodenames{PC0,PC4,PC1,PC2,PC3}

\tikzset{%
    N/.style={draw, rounded corners=0pt, thick, inner sep= 5pt, minimum size=0.6cm, font=\scriptsize, text=black},
    P1/.style={N, fill=white, align=center, text width=1.7cm, font=\bfseries},
    P2/.style={N, fill=white, font=\bfseries},
    P3/.style={N, fill=white, text width=2.4cm, font=\bfseries},
    P4/.style={N, fill=white, text width=3.7cm, font=\bfseries},
    P5/.style={N, fill=white, text width=5.8cm, font=\bfseries}
}
\foreach \x in {0,1,...,8} {
    \pgfmathsetmacro{\wCur}{\w3p*\x + \w4p}
    \pgfmathsetmacro{\wNext}{\w3p*\x + \w4p + \w5p}
    \ifnum \x<3
    \pgfmathsetmacro{\distOne}{10}
    \pgfmathsetmacro{\distTwo}{\wCur}
    \pgfmathsetmacro{\distThree}{\wNext}
    \fi
    \ifnum \x=3
    \pgfmathsetmacro{\distOne}{0}
    \pgfmathsetmacro{\distTwo}{\wCur}
    \pgfmathsetmacro{\distThree}{\wNext}
    \fi
    \ifnum \x>3
    \pgfmathsetmacro{\distOne}{\distOne + \distTwo + \distThree}
    \fi
    \pgfmathsetmacro{\hCur}{\h + (1-\x)*0.1}
    \pgfmathsetmacro{\hNext}{\h + (1-\x)*0.1}
    \node (PC\x) [P1] at ($(0, 0.5*\hCur + 0.5*\hNext)$) {\textsc{Stage \x}};
    \node at ($(0, \hCur + 0.8*\hNext + 0.2*\distOne + 0.1*\distTwo + 0.1*\distThree)$) {\textsc{\ifnum \x=3 Stage 0\else\ifnum \x>3 1\else 2\fi\fi}};
    \node at ($(0, \hCur - 0.2*\distOne + 0.1*\distTwo + 0.1*\distThree)$) {$H^{w,\bar{w}}$};
    \draw[dashed, thick, dashed, shorten >=1cm, shorten <=1cm] ($(0, \hCur + \distOne + \distTwo + \distThree)$) -- ($(0, \hNext - \distOne - \distTwo - \distThree)$);
    \draw[decorate, decoration={brace, mirror, amplitude=5pt}, thick] ($(0, \hCur - 0.2*\distOne - \distTwo - \distThree)$) -- ($(0, \hCur + \distOne + \distTwo + \distThree)$) node[midway, xshift=-2.8cm, yshift=0.2cm] {\ifnum \x=3 $5$\else 20\%\fi};
    \ifnum \x<3
    \draw[decorate, decoration={brace, amplitude=5pt}, thick] ($(0, \hCur + \distOne + \distTwo + \distThree)$) -- ($(0, \hCur - 0.2*\distOne + 0.1*\distTwo + 0.1*\distThree)$) node[midway, xshift=-1cm, yshift=0.2cm] {\ifnum \x=0 \ifnum \x<1 7\else 7\fi\fi};
    \else
    \draw[decorate, decoration={brace, amplitude=5pt}, thick] ($(0, \hCur - 0.2*\distOne + 0.1*\distTwo + 0.1*\distThree)$) -- ($(0, \hNext + \distOne + \distTwo - \distThree)$) node[midway, xshift=-1.9cm, yshift=0.2cm] {\ifnum \x>0 \ifnum \x<1 30\else 35\fi\fi};
    \fi
    \ifnum \x=3
    \draw[decorate, decoration={brace, amplitude=5pt}, thick] ($(0, \hCur - 0.2*\distOne + 0.1*\distTwo + 0.1*\distThree)$) -- ($(0, \hCur + \distOne + \distTwo + \distThree)$) node[midway, xshift=-1cm, yshift=0.2cm] {\ifnum \x>0 10\else 30\fi};
    \fi
    \draw[decorate, decoration={brace, amplitude=5pt}, thick] ($(0, \hCur + \distOne + \distTwo + \distThree)$) -- ($(0, \hNext - \distOne - \distTwo - \distThree)$) node[midway, xshift=2.8cm, yshift=0.2cm] {\ifnum \x>0 $2$\else 2\fi};
}
\end{tikzpicture}

\end{document}

