    \foreach \i in {1,2,3,4,5,6,7,8,9}{
        \draw (\i,1.5) rectangle (\i+4,6.5);  
        \draw (\i+4,4) rectangle (\i+8,6); 
        \draw (\i+8,5.5) rectangle (\i+12,7); 
