        \node at ({(\i+4)/2},6.7) {\small $H_{\i}$};
        \node at ({(\i+4)/2},4.7) {\small $G_{\i}$};
        \node at ({(\i+4)/2},1.7) {\small $L_{\i}$};
    }

    % Marginal costs
    \draw (1.5,1.5) rectangle (4+7,6.5-4);  % left rectangle
