\node[below=0pt of #5,anchor=north,outer sep=2pt] {$\frac{i}{L}$};
}
\newcommand{\projection}[4]{
\node[draw,minimum height=1cm,minimum width=2cm,fill=white,anchor=west,font=\footnotesize,outer sep=2pt] (P) at (#1,-#3) {#2};
