\pgfmathdeclarefunction{veclen}{2}{%
  \begingroup%
  \pgfmath@count@words{#1}%
  \edef\pgfmathresult{}\pgfmath@count@a
  \pgfutil@loop
    \ifnum\pgfmath@count@words>0
     \ifnum\pgfmath@count@\pgfmathresult pt > 0pt
       \advance\pgfmath@count@\pgfmathresult pt
     \fi
     \ifnum\pgfmath@count@words=\pgfmath@count@a
       \global\let\pgfmathresult\pgfmathresult
       \global\pgfmath@count@words=0
     \fi
    \fi
  \edef\pgfmathresult{\pgfmathresult}%
  \pgfmath@smuggleone\pgfmathresult\endgroup%
}%
