\begin{tikzpicture}[
    % redefine the decoration
    decorate,
    % make the style of the "error text" (here drawn) more flexible
    decorate,
    looseness=2,
    % set the label style
    every error text/.style={
        font=\small\color{blue},
        % add the label itself as node, which makes it possible to
        % draw a line from the "source" to the "label" without having
        % to draw it first in a node
        label={[inner sep=0pt]
                $(\pgfdecoratedpathfirst)
            node [pos=.8, \textbf{#1}] {}$}
    }] % add the error label as node as well
    % basically this just says draw the original path and labels it
    % with the given text
    \draw [
        ultra thick,
        draw=black,
        % adds a node at the mid point of the path to be decorated
        decorate,
        % sets the label text
        label={$x_{t+1} = \Delta x_{t} + \Delta y_{t}$}
    ]
        (0, 0) to[bend left] node [pos=.8] {$\Delta y_{t}$} (10, 0);

    % draw the ticks and labels as well
    \foreach \x / \l in {0/0, 2.5/, 5/+, 7.5/, 10/} {
        \draw [thick] (\x, 1ex) -- ++(0, -2ex) node [below, label={[inner sep=0pt]\l:\textbf{$x_{t+\x}$}}] {};
    }
    \foreach \x / \l in {0/-4, 2.5/, 5/-+, 7.5/, 10/} {
        \draw [thick] (\x, 1ex) -- ++(0, -2ex) node [below, label={[inner sep=0pt]\l:\textbf{$x_{t+\x}$}}] {};
    }
    % draw the middle line with the down arrow as well
    \draw [thick, decorate] (1, -2) -- ++(2, 0);

    \begin{scope}[yshift=-3cm]
        % do the same as above, only here the nodes are drawn
        % first
        \draw [
            decorate,
            % because we now draw the nodes, we need to change
            % the decoration to "walk" over the nodes
            decorate,
            % set the style of the nodes, e.g. the label style,
            % and because we don't need the `inner sep` any more we
            % just set it to zero
            label=,
        ]
            % this is the upper "offset node"
            (0, 1) to[bend left] node [pos=.8] {$c^{+} - \Delta y_{t} / 2$} (1, 0) % will draw only half of the line
                node [pos=0] (offset) {} % adds a node at the upper "offset"
            % this is the lower "offset node"
            (10, 1) to[bend right] node [pos=.8] {$c^{+} - \Delta y_{t} / 2$} (9, 0) % will draw only half of the line
                node [pos=0] (offset) {} % adds a node at the lower "offset"
        % Now we draw the line with the down arrow
        % It is drawn from the "offset node" to the "offset" node of the
        % previous "half line". Since we started from the upper "offset"
        % node, we end at the lower "offset node" as well.
        (offset.south) |- (offset.south east)
            node [pos=.33, below] {$\Delta y_{t + 1}$}
        ;
    \end{scope}

    % draw the first (left) error text
