			\node[draw=blue,fill=blue!20,right=of present] (deep)                                        {Loops in 1D dataset};
			\node[draw=blue,fill=blue!20,anchor=west] at ($(present.east) + (5.5cm,0)$)              {Law of large number: $\forall~\epsilon >0,\forall~b$ such that $\forall~s$};
