			\node[draw=blue,fill=blue!20,above=of deep] (FE)                                 {Law of small number: $\forall~\epsilon >0,\forall~b$ such that $\forall~s$};
			\node[draw=red,fill=red!20,anchor=west] at ($(LH.east) + (5.5cm,0)$)             {Define $\hat{\mathbf{y}}_{1D,b} = \left(\mathbf{y}_{1D,b} - \frac{\epsilon_{b}}{s}\right)$, with $\epsilon_{b}$ and $s$ defined above};
			\node[draw=red,fill=red!20,anchor=west] at ($(FE.east) + (5.5cm,0)$)              { Define $\mathbf{y}_{1D,b} = \left(\mathbf{y}_{1D,b} - \frac{\epsilon_{b}}{s}\right)$, with $\epsilon_{b}$ and $s$ defined above};


			% Box around "box" (for multiple boxes in sequence)
			\node [box,fit=(LH)(FE),inner sep=0pt,thick,draw=black!50] (all) {};


