			\node[draw=none,inner sep=0pt,align=left,fill=none,text width=4.5cm] at ($(all.north) + (0,3.5cm)$) {\textbf{Change has been made to the flow diagrams in order to ensure that the assumptions of the main result are met.}};

			% Flow diagrams
			% loopy
			\node [box,fit=(present)(deep),inner sep=0pt,thick,draw=black] (low) {
				\parbox{4.75cm}{Flow is ``loopy'' -- has loops \cite{LowFluc,low:nonlinear}.\par}
				\\
				\textcolor{blue}{Ensures that:}
					\begin{itemize}
						\item \textbf{Local:} Each entry in the dataset is influenced by the previous one\\
						\item \textbf{Global:} The sum of all entries in the dataset is the same everywhere.
					\end{itemize}
			};
			\node [box] at (low.south) (lowdescr) {See \cite{low:nonlinear} for a description of \textbf{low frequency} changes in a time series.};
			% deep
			\node [box,fit=(low)(LH)(FE),inner sep=0pt,thick,draw=black] (dial) {
				\parbox{3.75cm}{Network changes are ``dialable'' -- has the dial property.\par}
				\\
				\textcolor{blue}{Ensures that:}
					\begin{itemize}
						\item \textbf{Local:} At each point in time, the dataset is sampled at that point, i.e.\ the sampling frequency is infinite.\par
						\item \textbf{Global:} The sum of all entries in the dataset is the same, regardless of time.\par
					\end{itemize}
			};
			\node [box] at (dial.south) (dialdescr) {See \cite{dial:nonlinear} for a description of \textbf{dialable} changes in a time series.};


