\newcommand{\textbox}[5]{%
  \begin{tikzpicture}
    \node[textbox=\pgfkeysvalueof{/tikz/mycolor}] (textbox)
     {#2\\#3\\#4\\#5};
    \begin{scope}[node distance = 0mm, on chain, start chain=going below]
      \node[textbox=\pgfkeysvalueof{/tikz/mycolor}, label={[mylabel]#5}] {};
    \end{scope}
    \node[inner xsep=0pt, inner ysep=3pt, above=0mm of textbox] (filler) {\vphantom{#2}};
    \node[rectangle, rounded corners, draw=white, fill=white, inner sep=0pt,
          text width=\tvwidth, minimum height=\tvheight, fit=(textbox)] {};
  \end{tikzpicture}%
}

