\newcommand\ppbb{path picture bounding box}

\tikzset{%
  box/.style={draw, minimum height=7mm, outer sep=0pt},
  pics/Matrix/.style args={%
    #1 rows, #2 columns, #3 depth, #4 to #5/.rest={#6}}%
  ={code={%
      \path [draw, pic actions] 
        ({-sqrt(3)*#3/2}, 0 |- \ppbb.north) coordinate (-s)
        coordinate (#2-1 SW)  coordinate (#2-1 SE) 
        coordinate (#2-2 SW)  coordinate (#2-2 SE)
        (#3/2 -#3*sqrt(3)/2, 0 |- \ppbb.north) coordinate (-t)
        coordinate (#1-1 TV)  coordinate (#1-2 TV)
        coordinate (#2-1 NE)  coordinate (#2-2 NE)
        ;
        \path 
          \foreach \row in {1,..., #1} {        
            \foreach \col in {1,..., #2} {
              ($(\col*sqrt(3)-#3*sqrt(3)/2,-#3/2+sqrt(3)*\col*(#3-1)/2)$) +(-#3/2, -#3*sqrt(3)/4) coordinate (l-\row-\col)
              ($(\row*sqrt(3)-#3*sqrt(3)/2,-#3/2+sqrt(3)*(\row+1)/2)$) +(-#3/2, -#3*sqrt(3)/4) coordinate (r-\row-\col)
              }
          }
          ;
        \path 
          \foreach \row in {1,..., #1} {        
            \foreach \col [count=\z] in {#4} {
              \draw[thick, -stealth] (l-\row-\col) -- 
              node [swap, font=\footnotesize, above] {$\mathbf{y}_{\z}$} 
              (r-\row-\col);
            }   
          }
        }
        \path ($(-#3/2, 0) + (\ppbb.west)$) coordinate (o)
        (0,  \ppbb.south) coordinate (#1-1 SW)  coordinate (#1-1 NE)
        (0, -#3*sqrt(3)/2 -#3/2) coordinate (#1-2 SW)  coordinate (#1-2 NE)
        ;
        \path 
          \foreach \row in {1,..., #1} {        
            \foreach \col [count=\z] in {#5} {
              \draw[thick, -stealth] (o) -- 
              node [font=\footnotesize, above] {$\mathbf{u}_{\z}$} 
              ($(\col*sqrt(3)-#3*sqrt(3)/2,-#3/2+sqrt(3)*\col*(#3-1)/2)$)
              ;
            }   
          }
        ;
        \node [font=\footnotesize] 
          at ($(o)+(-.2,-.5*#3*sqrt(3)/2)$) {$\mathbf{0}$};
        }
  },
}

