\begin{document}
\begin{tikzpicture}[scale=1.5]
%Setting up the axes
\draw[thick,-stealth] (-3,0) -- (3,0) coordinate (x axis);
\draw[thick,-stealth] (0,-1) -- (0,3) coordinate (y axis);

%Drawing the two arcs
\draw[thick,blue] (0,0) arc (0:60:1);
\draw[thick,blue] (0,0) arc (0:30:2);

\draw[thick,red] (0,0) -- (30:2);
\draw[thick,red] (0,0) -- (60:1);

%Marking the angles
\path pic[draw, angle radius=5mm,"$\rho$",angle eccentricity=1.5] {angle=x axis--x quadrant--red quadrant};
\path pic[draw, angle radius=5mm,"$\theta$",angle eccentricity=1.5] {angle=y axis--y quadrant--blue quadrant};

%Marking the vertices of the triangle
\draw[fill] (0,0) circle (1pt) node[below] {$\textcolor{red}{x_i}$} -- (30:2) circle (1pt) node[above] {$\textcolor{blue}{x_j}$} -- (60:1) circle (1pt) node[above] {$\textcolor{black}{x_k}$} -- cycle;

%Defining the coordinates of P
\coordinate (P) at ($(x axis) + (60:2)$);
\path (P) ++(20:1) coordinate (aux1);
\path (P) ++(160:1) coordinate (aux2);

%Drawing the path that connects the vertices of the isosceles triangle
\draw[line width=1pt,latex-latex,shorten >=-4pt,shorten <=-4pt] (aux1) -- (aux2);

%Showing the lengths of some of the lines connecting the vertices of the isosceles triangle
\draw [decorate,decoration={brace,mirror,raise=3pt},thick,xshift=0.5pt,yshift=-0.5pt]
(P) -- (aux1) node[midway,yshift=-5pt]{$\alpha$};
\draw [decorate,decoration={brace,mirror,raise=3pt},thick,xshift=0.5pt,yshift=-0.5pt]
(P) -- (aux2) node[midway,yshift=-5pt]{$\alpha$};

%Showing the lengths of some of the lines connecting the points of the isosceles triangle
\draw [decorate,decoration={brace,mirror,raise=3pt},thick,xshift=-0.5pt,yshift=-3pt]
($(P)-(aux1)$) -- ($(P)-(aux2)$) node[midway,xshift=10pt]{$\rho$};
\draw [decorate,decoration={brace,mirror,raise=3pt},thick,xshift=-0.5pt,yshift=-3pt]
($(P)-(aux2)$) -- ($(P)-(aux1)$) node[midway,xshift=-10pt]{$\rho$};
\end{tikzpicture}
\end{document}

