        \foreach \y in {0,1,2}{
            \draw[dashed] (DPGCurrent\x\y) -- (CurrBlock\x\y.south east);
        }
    }

    %This loop draws the top braces
    \foreach \x in {0,1,2,3}{
        \foreach \y/\Height in {0/2,1/1}{
            \node[Brackets] (BrambleTopBrackets\x\y\Height) at ($(Pulse\x\y.north west)+(0,\Height pt)$) {};
        }
    }

    %This loop draws the bottom braces
    \foreach \x in {0,1,2,3}{
