    \node[BlueBlock, below left=2.25cm of CurrBlockLabel] (GCurrentLabel) {G\current};
    \node[RedBlock, below right=1.25cm of CurrBlockLabel] (PulsePulseLabel) {P\pulse};

    \foreach \x in {0,1,2,3}{
        \node[BlueBlock, below=1.5cm of GCurrentLabel] (CurrBlockLabel) {Current};
        \node[RedBlock, below=1.5cm of PulsePulseLabel] (DPGCurrentLabel) {DPG\current};
    }

    %Draw the labels above the left side of the left braces
    \node[BlueBlock, above left=0.25cm and -0.25cm of CurrBlock200] (VoltageControlLabel) {VoltageControl};
    \node[RedBlock, above left=0.25cm and -0.25cm of Pulse200] (PulseVoltageControlLabel) {Pulse\:VoltageControl};

    %Draw the labels above the right side of the right braces
    \node[BlueBlock, above right=0.25cm and -0.25cm of CurrBlock200] (VoltageShapingLabel) {Voltage\:Shaping};
    \node[RedBlock, above right=0.25cm and -0.25cm of DPGCurrent200] (DPGVoltageControlLabel) {DPG\:VoltageControl};

    %Draw the labels on the left side of the left braces
    \node[BlueBlock, above left=0.25cm and -0.25cm of VoltageControlLabel] (VoltageControlLabel) {Voltage\:Control};
    \node[RedBlock, above left=0.25cm and -0.25cm of PulseVoltageControlLabel] (PulseVoltageControlLabel) {Pulse\:VoltageControl};

    %Draw the labels on the right side of the right braces
    \node[BlueBlock, above right=0.25cm and -0.25cm of VoltageShapingLabel] (VoltageShapingLabel) {Voltage\:Shaping};
    \node[RedBlock, above right=0.25cm and -0.25cm of DPGVoltageControlLabel] (DPGVoltageShapingLabel) {DPG\:Voltage\:Shaping};

    %Draw the labels on the left and right side of the bottom braces
    \node[BlueBlock, above left=0.25cm and -0.25cm of GCurrentLabel] (VoltageControlLabel) {Voltage\:Control};
    \node[RedBlock, above left=0.25cm and -0.25cm of PulsePulseLabel] (DPGVoltageControlLabel) {DPG\:Voltage\:Control};

    %Draws the input and output layers of the bramble
    \foreach \x in {0,1,2,3}{
