            \draw[<->] ($(DPGCurrent\x\y)+(1.5cm,0)$) -- ($(PulsePulse\x\y)+(1.5cm,0)$);
            \draw[<->] ($(Pulse\x\y)+(1.5cm,0)$) -- ($(GCurrent\x\y)+(1.5cm,0)$);
        }
    }

