% the code for the auto-calming algorithm is in two main parts:
% 1. it handles the centering of the neurons and the calibration of the
%    neural network
% 2. it iteratively reduces the number of required neurons

\def\neuronNealer{%
    \foreach \x in {1,...,28} {%
      \coordinate (centerNeuron-\x) at (1.4cm,0.6cm);
      \coordinate (leftNeuron-\x) at (-2cm,0.6cm);
      \coordinate (rightNeuron-\x) at (3cm,0.6cm);
    }
    \foreach \x in {1,...,28} {%
      \pgfmathparse{Mod(\x-29,3)==0? 1:0}%\pgfmathresult
      \ifnum\pgfmathresult>0\relax
        \foreach \y in {\x,...,1} {%
          \pgfmathparse{Mod(\y+3,3)==0? 1:0}\ifnum\pgfmathresult>0\relax
            \node at (centerNeuron-\x) {$-$};
            \foreach \y in {\x,...,1} {
              \pgfmathparse{(\y<=\x)? 1:0}
              \ifnum\pgfmathresult>0\relax
                \node at (leftNeuron-\y) {$-$};
                \node at (centerNeuron-\x) {$-$};
                \node at (rightNeuron-\y) {};
              \else\ifnum\pgfmathresult<0\relax
                \node at (rightNeuron-\y) {$-$};
              \fi\fi
          }\fi\fi
        }
        \foreach \y in {1,...,1} {%
          \pgfmathparse{Mod(\y+3,3)==0? 1:0}\ifnum\pgfmathresult>0\relax
            \node at (centerNeuron-\x) {$-$};
          \fi
        }
      \else\ifnum\pgfmathresult<0\relax
        \foreach \y in {\x,...,1} {%
          \pgfmathparse{(\y<=\x)? 1:0}\ifnum\pgfmathresult>0\relax
            \node at (leftNeuron-\y) {$-$};
            \node at (centerNeuron-\x) {};
            \node at (rightNeuron-\y) {};
          \else\ifnum\pgfmathresult<0\relax
            \node at (rightNeuron-\y) {$-$};
          \fi\fi
        }
      \fi\fi
    }
    \foreach \x in {1,...,28} {%
      \ifnum\pgfmathresult>0\relax
        \node at (centerNeuron-\x) {};
      \else
        \node at (centerNeuron-\x) {$\vdots$};
      \fi
    }
}
