% Three counters
\newcounter{x}
\newcounter{y}
\newcounter{delta}

% The angles of x,y and delta
\newcommand\xangle{30}
\newcommand\yangle{-15}
\newcommand\deltaangle{45}

% The top side of a pentagon
\newcommand\topside[2]{
  \fill[fill=cyan!50,even odd rule] #1 -- ++(\xangle+90:\deltaangle/2) coordinate(p#2) -- ++(\xangle-90:\deltaangle/2) -- cycle #1 -- ++(\yangle-90:\yangle/2) -- ++(\yangle+90:\yangle/2) -- cycle;
}

% The left side of a pentagon
\newcommand\leftside[2]{
  \fill[fill=green!30,even odd rule] #1 -- ++(\xangle+90:\deltaangle/2) -- ++(\xangle-90:\deltaangle/2) -- cycle #1 -- ++(\yangle+90:\yangle/2) -- ++(\yangle-90:\yangle/2) -- cycle;
}

% The right side of a pentagon
\newcommand\rightside[2]{
  \fill[fill=yellow!50,even odd rule] #1 -- ++(\xangle-90:\deltaangle/2) -- ++(\xangle+90:\deltaangle/2) -- cycle #1 -- ++(\yangle-90:\yangle/2) -- ++(\yangle+90:\yangle/2) -- cycle;
}

% The graph G
\newcommand\graph[1]{
\begin{scope}[spy using outlines={ellipse, magnification=4, size=8cm, connect spies}]
  \path #1;
  \spy [red,dashed, magnification=4] on (\xangle+90:\deltaangle/2) in node [below] at ($(current bounding box.north) + (0,-1cm)$);
\end{scope}
}

% The graph g transformed
\newcommand\gtransformed[1]{
  \begin{scope}[transform shape]
    \path #1;
  \end{scope}
}

% Three new dimensions
\newcommand\pgfmathsetmacro[2]{\pgfmathparse{#2}\expandafter\pgfmathsetmacro}

% The scale factor
\newcommand\scalefactor{\pgfmathparse{1}\pgfmathprintnumber{\pgfmathresult}}

% The zooming in direction
\newcommand\dirzoom[2][1]{
  \scalefactor#2,
  \dirzoomhelper[#1]
}

% The helper for the direction of the zooming in
\newcommand\dirzoomhelper[1]{
  \coordinate (aux) at (1,1);
  \draw[|-|,shorten >=-2pt,shorten <=-2pt] (aux) -- ++(180:#1) node[midway,left] {$\scriptstyle #1$};
}

