% Define the packages used for graphics
\usepackage{graphicx}
\usepackage{tikz}
\usetikzlibrary{plotmarks}

% Three counters
\newcounter{x}
\newcounter{y}
\newcounter{z}

% The labels for the three counters
\newcommand{\xlab}{\textbf{x}}
\newcommand{\ylab}{\textbf{y}}
\newcommand{\zlab}{\textbf{z}}

% The font to be used for the labels
\newcommand{\mylabfont}{\sffamily}

\begin{tikzpicture}
  % Start the axis for the first plot
  \begin{axis}[
    % set the size of the axis
    width=8cm, height=6cm,
    % restrict y and x to domain 0:10
    domain=0:10,
    % remove samples at -1
    samples=-1,
    % plot only the data from point 0 to 1
    ycomb,
    % give the first plot an name
    name=firstplot,
    % this and the next two plots have the same axis environment
    xshift=10cm,
    % give the plots to 2 axes environments
    % (in each case the first plot is in the domain 0:1, the
    %  second starts at 1)
    axis on top,
    % make the first axis narrower (but also at the same time increase
    % the counter x)
    width=4cm,
    enlarge x limits=0.2,
    % give the first axis labels
    xlabel={\xlab},
    % give the second axis labels
    ylabel={\ylab},
    ymajorgrids,
    xmajorgrids,
    % the following two counters are reset to 0 each time a new
    % plot is initiated
    counter=0,
    % the following three counters are incremented
    counter=1,
    % the plot is repeated from the beginning (so as to add the
    % corresponding y value to the plot)
    % (the y value is added to the first plot as 1)
    % (the table is not closed; the last \addplot command is
    %   added to the last axis environment)
    table/y expr=1+\thiscounter, 
    % (the \addplot command is used again to add the values to the
    %  plot)
    add plot={
      % values are not stored in a table; therefore, a cycle is used
      \foreach \i in {1,..., 4} {
        % If the value is 1, draw it; otherwise add it to the counter
        \ifnum\thiscounter=1
          \addplot+[mark=square*, thick, black, mark options={fill=blue}] plot (\i, {1+3*\thiscounter});
        \else
          \addplot+[mark=square*, thick, black] plot (\i, {1+\thiscounter});
        \fi
      }
    },
    % close the table
    table/closed=false,
    % the following three counters are set to 1 each time a new
    % plot is initiated
    counter=2,
    % the plot is repeated from the beginning (so as to add the
    % corresponding y value to the plot)
    % (the table is not closed; the last \addplot command is
    %   added to the last axis environment)
    table/y expr=1+\thiscounter, 
    % (the \addplot command is used to add the values to the plot)
    add plot={
      \addplot+[mark=square*, thick, black, mark options={fill=red}] plot (\x, {2+3*\thiscounter});
    },
    % give the final plot an index
    no markers,
  ]

  \end{axis}

  % Start the axis for the second plot
  \begin{axis}[
    % set the size of the axis
    width=4cm, height=6cm,
    axis x line=none,
    axis y line=none,
    % plot only the data from -1 to 1
    domain=-1:1,
    % give the first plot labels
    xlabel={\xlab},
    ylabel={\ylab},
    ymajorgrids,
    xmajorgrids,
    % the counter x is set to 0 at the start of the axis
    counter=0,
    % the plot is repeated from the beginning (so as to add the
    % corresponding y value to the plot)
    table/y expr=1+\thiscounter, 
    % (the table is not closed; the last \addplot command is
    %   added to the last axis environment)
    add plot={
      \foreach \i in {1,..., 4} {
        \addplot+[thick, black] plot (\i, {4*\thiscounter});
      }
    },
    % give the final plot an index
    no markers,
  ]

  \end{axis}

  % Start the axis for the third plot
  \begin{axis}[
    % set the size of the axis
    width=4cm, height=6cm,
    % plot only the data from -1 to 1
    domain=-1:1,
    % give the first plot labels
    xlabel={\xlab},
    ylabel={\ylab},
    ymajorgrids,
    xmajorgrids,
    % the counter x is set to 1 at the start of the axis
    counter=1,
    % the plot is repeated from the beginning (so as to add the
    % corresponding y value to the plot)
    table/y expr=1+\thiscounter, 
    % (the table is not closed; the last \addplot command is
    %   added to the last axis environment)
    add plot={
      \foreach \i in {1,..., 4} {
        \addplot+[thick, black] plot (\i, {5*\thiscounter});
      }
    },
    % give the final plot an index
    no markers,
  ]

  \end{axis}

  % Add the legend entries
  % (The `\addlegendimage' command was added in v1.2 of pgfplots,
  %  while the `legend image code' is from v1.3)
  \addlegendimage{legend image code/.code={\node at (0.5,0.9) {1};}}
  \addlegendentry{\mylabfont1}
  \addlegendimage{thick, blue}
  \addlegendentry{\mylabfont2}
  \addlegendimage{legend image code/.code={\node at (0.5,0.1) {2};}}
  \addlegendentry{\mylabfont3}
  \addlegendimage{thick, red}
  \addlegendentry{\mylabfont4}

\end{tikzpicture}

\end{document}

